mapper.insert:
  		
  		SELECT concat('ORD',(TO_CHAR(sysdate, 'yyymmddhh24mmss'))) FROM DUAL
  		
  		
			INSERT INTO tbl_order (ono, id, name, sno) VALUES (
			    #{ono}, 
			    #{id},    
			    (
			    SELECT DISTINCT S.name 
			    FROM tbl_store S
			    JOIN tbl_menu M
			    USING (sno)
			    WHERE sno = #{sno}
			    ),
			    #{sno}
			)

mapper.update:

 		UPDATE tbl_order set
 		status = 'canceled'
 		WHERE ono = #{ono}


 		UPDATE tbl_order set
 		status = 'paid'
 		WHERE ono = #{ono}

  	UPDATE tbl_order SET
	AMOUNT = (
	SELECT sum(price)
	FROM tbl_orderMenu
	where ono = #{ono}
	)where ono = #{ono}
