mapper.update:
UPDATE tbl_payment SET
status = #{status}
WHERE ono = #{ono}

mapper.select:

SELECT RN, timestamp, ono, name, amount FROM (
	SELECT
		/*+ INDEX_DESC(tbl_payment payment_pk) */
		ROWNUM RN, timestamp, ono, name, amount
		FROM tbl_payment
		WHERE ROWNUM <= #{pageNum} * #{pageAmount}
) WHERE RN > (#{pageNum}-1) * #{pageAmount};
